        <StyledTextField  className='reg-from-input'  id="standard-basic" label="Name" variant="standard"
             onChange={(e) => setname(e.target.value)}  value={usename}
        />
        <StyledTextField  className='reg-from-input' id="standard-basic" label="Email" variant="standard" 
           onChange={(e) => setemail(e.target.value)}
          
           value={email}
        />
        <StyledTextField
          id="standard-password-input"
          label="Password"
          onChange={(e) => setpassword(e.target.value)}
       
          value={password}
          type="password" className='reg-from-input' 
          autoComplete="current-password"
          variant="standard"
          />  
        <Link to="/login" className="auth-link">
        <StyledButton className='reg-from-input auth-button' variant="contained" color="success"
       
       >Register</StyledButton>
        </Link>   
       </form>
        
        
           </div>
        <p>Already have an account?</p>
        <Link  >Login</Link>
    </div>
   </div>
   </>
  )
}

export default Register
